import React from 'react'
import Root from './style'
import Button from '../UIComponent/Button.js'
import Input from '../UIComponent/Input.js'
import { connect } from 'react-redux'
import { bindActionCreators } from 'redux'
import { EditTodo, AddTodo, UpdateTodo } from '../../../Actions/todo'

const mapDispatchToProps = dispatch => {
    return bindActionCreators({ AddTodo, EditTodo, UpdateTodo }, dispatch)
}
const mapStateToProps = state => ({
    model: state.model
});
class TodoForm extends React.Component {
    state = {
        modelState: null,
    }
    componentDidMount() {
        const { model } = this.props;
        if (model !== null) {
            this.setState({
                modelState: model ? { ...model } : null,
            })
        }
    }
    componentDidUpdate(preProps) {
        const { model } = this.props;
        if (preProps.model !== model) {
            this.setState({
                modelState: model ? { ...model } : null,
            })
        }
    }
    handleInput = (event) => {
        const { model } = this.props;
        this.setState({
            modelState: { ...model, content: event.target.value }
        })
    }
    handleAdd = () => {
        const { modelState } = this.state;
        const { AddTodo } = this.props
        console.log(modelState.content === null )
         AddTodo({
            id: Math.random(),
            content: modelState.content,
        })
        this.setState({
            modelState: null,
        })

    }
    handleUpdate = async () => {
        const { modelState } = this.state;
        const { UpdateTodo } = this.props;
        UpdateTodo(modelState);
    }
    handleCancel = () => {
        this.props.EditTodo(null);
    }
    render() {
        const { modelState } = this.state;
        const { model } = this.props;
        return (
            <Root className="gutter-8">
                <div style={{ width: "100%" }}>
                    <Input placeholder="Task" type="text" value={modelState ? modelState.content : ''} onChange={this.handleInput} />
                </div>
                <div >
                    {!model ?
                        <Button type={'add'} onClick={this.handleAdd}>Add</Button> :
                        <div style={{ display: "flex" }} className='gutter-8'>
                            <Button type={'update'} onClick={this.handleUpdate}>Update</Button>
                            <Button type={'cancle'} onClick={this.handleCancel} >Cancel</Button>
                        </div>
                    }
                </div>
            </Root>
        )
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(TodoForm);
