import React from 'react'
import Button from '../UIComponent/Button'
import { connect } from 'react-redux'
import {EditTodo} from '../../../Actions/todo'

const mapDispatchToStores = dispatch => ({
    EditTodo: item => dispatch(EditTodo(item))
})

class TodoEdit extends React.Component {
    handleEdit = () => {
        const { data } = this.props;
        this.props.EditTodo(data);
    }
    render() {
        const { disabled } = this.props
        return (
            <div>
                <Button type='edit' disabled={disabled} onClick={this.handleEdit}>Edit</Button>
            </div>
        )
    }
}

export default connect(null, mapDispatchToStores)(TodoEdit);
