import { ADD_TODO, EDIT_TODO, DELETE_TODO } from '../Actions/todo'

const initialState = {
    todoList: [{
        id: 12,
        content: "dmmm",
    },
    {
        id: 3,
        content: "hey",
    }]
}

function todoApp(state = initialState, action) {
    switch (action.type) {
        case ADD_TODO:
            console.log(action.data)
            return {
                ...state,
                todoList: [...state.todoList, action.data],
            }
        case EDIT_TODO:
            console.log(state)
            const { todoList1 } = state.todoList;
            const todoListTemp = todoList1.slice()
            const index = todoList.findIndex(item => item.id === action.data.id)
            todoListTemp[index] = action.data;
            return (
                {
                    todoList: todoListTemp,
                }
            )
        case DELETE_TODO:
            return {
                todoList: state.todoList.filter(item => item.id !== action.data.id)
            }
        default:
            return state;
    }
};

export default todoApp;
